import React, { useState, Fragment } from 'react'
import './Functionalities.css'
import Checkbox from "@material-ui/core/Checkbox";


export default function Functionalities() {
  const [isBlurred, setIsBlurred] = useState(false);
  const [isGrayscaled, setIsGrayscaled] = useState(false);

  const handleBlurr = (e) => {
    setIsBlurred(!isBlurred);
  }

  const handleGrayscale = (e) => {
    setIsGrayscaled(!isGrayscaled);
  }
  return (
    <Fragment>
        <section className="section" id='grey'>
            <p>Avec notre produit, vous pourrez floutter n'importe quelle image en un seul clic.</p>
            <Checkbox
                onChange={handleBlurr}
            />
            <img src="/Images/lion.jpeg" className={isBlurred ? "blurred" : ""} alt="lion" />
        </section>
        <hr className='trait' />
        <section className='section'>
            <p>Avec notre produit, vous pourrez floutter n'importe quelle image en un seul clic.</p>
            <Checkbox
                onChange={handleGrayscale}
            />
            <img src="/Images/lion.jpeg" className={isGrayscaled ? "grayscaled" : ""} alt="lion" />
        </section>
    </Fragment>
  )
}
